-- --------------------------------------------------------

--
-- Структура таблицы `wp_posts`
--

DROP TABLE IF EXISTS `wp_posts`;
CREATE TABLE `wp_posts` (
  `ID` bigint(20) UNSIGNED NOT NULL,
  `post_author` bigint(20) UNSIGNED NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_title` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_excerpt` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_status` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'open',
  `post_password` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `post_name` varchar(200) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `to_ping` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `pinged` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_parent` bigint(20) UNSIGNED NOT NULL DEFAULT '0',
  `guid` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_520_ci;

--
-- Дамп данных таблицы `wp_posts`
--

INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2018-03-18 15:59:05', '2018-03-18 12:59:05', '&nbsp;\r\n\r\nНемецкая марка Audi, представленная более чем на 100 рынках, является одним из мировых лидеров по производству автомобилей премиального сегмента. В состав концерна Audi AG входят также производитель итальянских суперкаров Lamborghini и компания по производству мотоциклов Ducati.\r\nИстория марки уходит своими корнями во времена зарождения автомобилестроения и тесно связана с именами великих «классиков» : инженеров, дизайнеров, гонщиков и конструкторов, — в разные годы на протяжении последних ста с лишним лет способствовавших ее успеху и развитию.\r\n\r\nМиллионам людей во всем мире сегодня хорошо знаком слоган компании — Vorsprung durch Technik. С немецкого на русский язык это можно перевести как «Превосходство высоких технологий». И на самом деле технические инновации, прогрессивные решения, стремление к смелым экспериментам и индивидуальный подход к каждому клиенту служат ориентирами для Audi. Ярким воплощением технологического превосходства марки является легендарный полный привод quattro, который в качестве опции доступен сегодня практически на всех моделях. Он не только дарит совершенно иные ощущения от управления автомобилем, но и повышает безопасность вождения, дает чувство уверенности на дороге и позволяет управлять автомобилем в спортивном стиле.\r\n\r\nПриверженность спорту является еще одной важной составляющей философии марки. Многочисленные победы в классическом ралли, на кольцевых гонках, в чемпионате DTM и в гонках на выносливость дали инженерам и конструкторам бесценный опыт, который сегодня воплощается в серийных моделях. Изысканный дизайн в сочетании со спортивным стилем — это одна из отличительных особенностей автомобилей Audi. Впрочем, из почти трех десятков актуальных моделей Audi как минимум семь можно без натяжки назвать спорткарами. За разработку и создание этих спортивных моделей с индексами R и RS отвечает специальное подразделение Audi Sport.\r\n\r\nМногочисленные независимые исследования, проводимые по всему миру, год от года демонстрируют неизменный успех Audi. Как и первые места во всевозможных рейтингах безопасности, на конкурсах потребительских симпатий и предпочтений.', 'Что такое Audi?', '', 'publish', 'open', 'open', '', 'privet-mir', '', '', '2018-03-18 17:45:43', '2018-03-18 15:45:43', '', 0, 'http://test.loc/?p=1', 0, 'post', '', 1),
(2, 1, '2018-03-18 15:59:05', '2018-03-18 12:59:05', '<h3 class=\"m_intro__headline h1\"><b>Захватывающе стремителен.</b></h3>\r\n<div class=\"m_intro__copy intro\">\r\n<div class=\"x_richtext\">\r\n<div>Благодаря мощному пятицилиндровому двигателю TFSI, выдающему 400 л. с. мощности и до 480 Н·м крутящего момента, Audi RS 3 Sportback всего за 4,1 секунды «выстреливает» до скорости 100 км/ч. Во внешности мощь нового Audi RS 3 Sportback подчеркивает решетка Singleframe черного глянцевого цвета в виде сот, а также агрессивный дизайн переднего бампера с большими воздухозаборниками и угловатыми декоративными накладками.</div>\r\n</div>\r\n</div>\r\n<div><img class=\"alignleft size-medium wp-image-14\" src=\"http://test.loc/wp-content/uploads/2018/03/1920x1080_ARS3_161005-300x169.jpg\" alt=\"\" width=\"300\" height=\"169\" /></div>', 'RS 3 Sportback', '', 'publish', 'closed', 'open', '', 'rs-3-sportback', '', '', '2018-03-18 16:51:26', '2018-03-18 14:51:26', '', 0, 'http://test.loc/?page_id=2', 0, 'page', '', 0),
